  return (-178*sqrt((x + 613)^2/20164 - 1) + 131);
}

int i, n = 10;

for (i = -n; i <= n; ++i) {
  draw((i,-n)--(i,n),gray(0.7));
  draw((-n,i)--(n,i),gray(0.7));
}

draw((0,-n)--(0,n));
draw((-n,0)--(n,0));

