problem:
Below is the graph of $y = a \csc bx$ for some positive constants $a$ and $b.$  Find $b.$

[asy]import TrigMacros;

size(500);

real g(real x)
{
	return 2*csc(x/39);
}

draw(graph(g,-78*pi + 0.01, -39*pi - 0.01),red);
draw(graph(g,-39*pi + 0.01,-0.01),red);
draw(graph(g,0.01,39*pi - 0.01),red);
draw(graph(g,39*pi + 0.01,78*pi - 0.01),red);
limits((-78*pi,-5),(78*pi,5),Crop);
draw((-39*pi,-5)--(-39*pi,5),dashed);
draw((39*pi,-5)--(39*pi,5),dashed);
trig_axes(-78*pi,78*pi,-5,5,pi/2,1);
layer();
rm_trig_labels(-155, 155, 2);

label("$1$", (0,1), E);
label("$2$", (0,2), E);
label("$3$", (0,3), E);
label("$4$", (0,4), E);
label("$-1$", (0,-1), E);
label("$-2$", (0,-2), E);
label("$-3$", (0,-3), E);
label("$-4$", (0,-4), E);
[/asy]
