xaxis("$x$",-0.96,20.96,Ticks(laxis,Step=1.0,Size=2,OmitTick(0)),Arrows(6),above=true); yaxis("$y$",-7.66,11.38,Ticks(laxis,Step=1.0,Size=2,OmitTick(0)),Arrows(6),above=true); draw((2,2)--(10,-7),linewidth(1.2)); draw((10,2)--(15,2),linewidth(1.2)); draw((15,10)--(18,-4),linewidth(1.2)); filldraw(circle((15,10),0.08),white); label("$ f(x) $",(0.5,4.3),SE*lsf);

