problem:
Below is the graph of $y = a \sin bx$ for some constants $a < 0$ and $b > 0.$  Find $b.$

[asy]import TrigMacros;

size(400);

real g(real x)
{
	return (-2*sin(x/28));
}

draw(graph(g,-28*pi,28*pi,n=700,join=operator ..),red);
trig_axes(-28*pi,28*pi,-3,3,pi/2,1);
layer();
rm_trig_labels(-55, 55, 2);

label("$1$", (0,1), E);
label("$2$", (0,2), E);
label("$-1$", (0,-1), E);
label("$-2$", (0,-2), E);
[/asy]
