[asy]
draw((0,0)--(4,0)--(2,3)--cycle,black+linewidth(1));
MarkAngle((4,0),(0,0),(2,3),1,black+linewidth(1));
MarkAngle((2,3),(4,0),(0,0),1,black+linewidth(1));
label("$A$",(2,3),N);
label("$B$",(0,0),W);
label("$C$",(4,0),E);
label("4",(0,0)--(4,0),S);
[/asy]
